import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { api } from "@shared/routes";
import { z } from "zod";
import multer from "multer";
import path from "path";
import fs from "fs";
import express from "express";
import { setupAuth, registerAuthRoutes, isAdminLeader } from "./replit_integrations/auth";

// Configure multer for file uploads
const uploadDir = path.join(process.cwd(), "uploads");
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir);
}

const upload = multer({
  storage: multer.diskStorage({
    destination: uploadDir,
    filename: (req, file, cb) => {
      const uniqueSuffix = Date.now() + "-" + Math.round(Math.random() * 1e9);
      cb(null, uniqueSuffix + path.extname(file.originalname));
    },
  }),
  limits: {
    fileSize: 5 * 1024 * 1024, // 5MB limit
  },
});

export async function registerRoutes(
  httpServer: Server,
  app: Express
): Promise<Server> {
  // Auth Setup
  await setupAuth(app);
  registerAuthRoutes(app);

  // Serve uploaded files
  app.use("/uploads", express.static(uploadDir));

  // === Content Routes ===
  app.get(api.content.getPage.path, async (req, res) => {
    const { page } = req.params;
    const sections = await storage.getSectionsByPage(page);
    res.json(sections);
  });

  app.post(api.content.updateSection.path, isAdminLeader, async (req, res) => {

    const { page, sectionKey } = req.params;
    const { content } = req.body;

    // Using the safe update method since unique constraints might be tricky in lite build
    const section = await storage.updateSectionSafe(page, sectionKey, content);
    res.json(section);
  });

  // === Event Routes ===
  app.get(api.events.list.path, async (req, res) => {
    const events = await storage.getEvents();
    res.json(events);
  });

  app.post(api.events.create.path, isAdminLeader, async (req, res) => {

    try {
      const input = api.events.create.input.parse(req.body);
      const event = await storage.createEvent(input);
      res.status(201).json(event);
    } catch (err) {
      if (err instanceof z.ZodError) {
        return res.status(400).json({ message: err.errors[0].message });
      }
      throw err;
    }
  });

  app.put(api.events.update.path, isAdminLeader, async (req, res) => {

    try {
      const input = api.events.update.input.parse(req.body);
      const event = await storage.updateEvent(Number(req.params.id), input);
      if (!event) return res.status(404).json({ message: "Event not found" });
      res.json(event);
    } catch (err) {
      if (err instanceof z.ZodError) {
        return res.status(400).json({ message: err.errors[0].message });
      }
      throw err;
    }
  });

  app.delete(api.events.delete.path, isAdminLeader, async (req, res) => {

    await storage.deleteEvent(Number(req.params.id));
    res.status(204).send();
  });

  // === Media Routes ===
  app.get(api.media.list.path, async (req, res) => {
    const items = await storage.getMedia();
    res.json(items);
  });

  app.post(api.media.upload.path, isAdminLeader, upload.single("file"), async (req, res) => {

    if (!req.file) return res.status(400).json({ message: "No file uploaded" });

    const item = await storage.createMedia({
      url: `/uploads/${req.file.filename}`,
      filename: req.file.originalname,
      mimeType: req.file.mimetype,
    });

    res.status(201).json(item);
  });

  app.delete(api.media.delete.path, isAdminLeader, async (req, res) => {

    const id = Number(req.params.id);
    const item = await storage.getMediaItem(id);
    if (!item) return res.status(404).json({ message: "Media not found" });

    // Try to delete file from disk
    const filePath = path.join(uploadDir, path.basename(item.url));
    if (fs.existsSync(filePath)) {
      fs.unlinkSync(filePath);
    }

    await storage.deleteMedia(id);
    res.status(204).send();
  });

  // === Settings Routes ===
  app.get(api.settings.list.path, async (req, res) => {
    const settings = await storage.getSettings();
    res.json(settings);
  });

  app.post(api.settings.update.path, isAdminLeader, async (req, res) => {

    const input = api.settings.update.input.parse(req.body);
    const settings = await storage.updateSettings(input);
    res.json(settings);
  });

  // === Dashboard Shortcuts Routes ===
  app.get(api.shortcuts.list.path, async (req, res) => {
    const shortcuts = await storage.getShortcuts();
    res.json(shortcuts);
  });

  app.post(api.shortcuts.create.path, isAdminLeader, async (req, res) => {

    try {
      const input = api.shortcuts.create.input.parse(req.body);
      const shortcut = await storage.createShortcut(input);
      res.status(201).json(shortcut);
    } catch (err) {
      if (err instanceof z.ZodError) {
        return res.status(400).json({ message: err.errors[0].message });
      }
      throw err;
    }
  });

  app.delete(api.shortcuts.delete.path, isAdminLeader, async (req, res) => {

    await storage.deleteShortcut(Number(req.params.id));
    res.status(204).send();
  });

  // === PCO Integration (Mock for now) ===
  app.post("/api/pco/sync", isAdminLeader, async (req, res) => {

    // In a real implementation, this would fetch from PCO API
    // For now, let's just return a success message
    res.json({ message: "Synced successfully (Mock)", count: 0 });
  });

  // === Seed Data (Init) ===
  await seedDatabase();

  return httpServer;
}

